jsScript.jsScript_script:
//Filtrar caracteres prohibidos en el Orion CB

var forbiddenChars = function (entidad) {
	entidad = entidad + ""; // force String type
	entidad = entidad.replace(/\(/g,"%28");
	entidad = entidad.replace(/\)/g,"%29");
	entidad = entidad.replace(/</g,"%3C");
	entidad = entidad.replace(/=/g,"%3D");
	entidad = entidad.replace(/>/g,"%3E");
	entidad = entidad.replace(/;/g,"%3B");
	entidad = entidad.replace(/'/g,"%27");

	return entidad;
}


